mapper.select:
select seq, id, ref, step, depth, title, content, wdate, del, readcount
from review
where seq=#{seq}

mapper.update:
update review
set title=#{title}, content=#{content}
where seq=#{seq}

mapper.delete:
update review
set del=1
where seq=#{seq}

mapper.update:
update review
set readcount=readcount+1
where seq=#{seq}
